import React, { useEffect, useState } from 'react';
import './Header.scss';
import logo from '../assets/logo.png';
import menu from '../assets/menu.png'
const Header = () => {

    const [menu, setMenu] = useState(false);

    useEffect(() => {
        const windowWidth = document.documentElement.clientWidth;
        windowWidth > 1050? setMenu(true) : setMenu(false);
        window.addEventListener("resize", getDimensions);
    }, []);

    return(
        <div id='header-container'>
            <div id="inner-header">
                <div id='logo'>
                    <img id='image' src={logo} alt="icon" />
                </div>
                <div id='menu-item'>
                    {menu&&<img id='image2' src={menu} alt="icon" />}
                    <div id='menu-list'>
                        <div className='menu-text'>Home</div>
                        <div className='menu-text'>About</div>
                        <div className='menu-text'>Contact</div>
                        <div className='menu-text'>FAQ</div>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default Header;
